import React, { useEffect, useState } from "react";
import Post from "./Post";
import { Box } from '@mui/material';

function Posts() {
  const [posts, setPosts] = useState([]);
  const [newTitle, setNewTitle] = useState("");
  const [newContent, setNewContent] = useState("");
  const [newAuthor, setNewAuthor] = useState("");

  const fetchPosts = async () => {
    const response = await fetch("/posts");
    const data = await response.json();
    setPosts(data);
  };

  const createPost = async (e) => {
    e.preventDefault();
    const response = await fetch("/posts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ title: newTitle, content: newContent, author: newAuthor }),
    });
    const data = await response.json();
    setPosts([data, ...posts]);
    setNewTitle("");
    setNewContent("");
    setNewAuthor("");
  };

  const updatePost = async (id, title, content, author) => {
    const response = await fetch(`/posts/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ title, content, author }),
    });
    const data = await response.json();
    setPosts(posts.map((post) => (post._id === id ? data : post)));
  };

  const deletePost = async (id) => {
    const response = await fetch(`/posts/${id}`, {
      method: "DELETE",
    });
    if (response.ok) {
      setPosts(posts.filter((post) => post._id !== id));
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <div>
      <h2>Posts</h2>
      <Box sx={{ mb: 2, width: '70%', mx: 'auto' }}>
        <Box sx={{ mb: 1 }}>
          <input
            type="text"
            value={newAuthor}
            onChange={(e) => setNewAuthor(e.target.value)}
            placeholder="Author"
            required
          />
        </Box>
        <Box sx={{ mb: 1 }}>
          <input
            type="text"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
            placeholder="Title"
            required
          />
        </Box>
        <textarea
          value={newContent}
          onChange={(e) => setNewContent(e.target.value)}
          placeholder="Content"
          required
        />
        <Box sx={{ mt: 1 }}>
          <button type="submit" onClick={createPost}>Add New Post</button>
        </Box>
      </Box>
      {posts.map((post) => (
        <Post key={post._id} post={post} editPost={updatePost} deletePost={deletePost} />
      ))}
    </div>
  );
}

export default Posts;
