import React, { useState } from "react";
import { TextField, Button, Typography, Box } from "@mui/material";

function Post({ post, editPost, deletePost }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(post.title);
  const [content, setContent] = useState(post.content);
  const [author, setAuthor] = useState(post.author);

  const handleSave = () => {
    editPost(post._id, title, content, author);
    setIsEditing(false);
  };

  return isEditing ? (
    <Box component="form" sx={{ mb: 4, mt: 2 }}>
      <TextField
        label="Author"
        type="text"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        fullWidth
        variant="outlined"
        required
        sx={{ mb: 2 }}
      />
      <TextField
        label="Title"
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        fullWidth
        variant="outlined"
        required
        sx={{ mb: 2 }}
      />
      <TextField
        label="Content"
        value={content}
        onChange={(e) => setContent(e.target.value)}
        fullWidth
        variant="outlined"
        required
        sx={{ mb: 2 }}
      />
      <Box sx={{ mt: 2, display: 'flex', justifyContent: 'space-between' }}>
        <Button onClick={handleSave} variant="contained">Save</Button>
        <Button onClick={() => setIsEditing(false)} variant="contained">Cancel</Button>
      </Box>
    </Box>
  ) : (
    <Box sx={{ mb: 4 }}>
      <Typography variant="h6" align="center" gutterBottom>{post.title}</Typography>
      <Typography variant="subtitle2" align="right" gutterBottom>{post.author}</Typography>
      <Typography variant="body1" align="center" gutterBottom>{post.content}</Typography>
      <Box sx={{ mt: 2, display: 'flex', justifyContent: 'space-between' }}>
        <Button onClick={() => setIsEditing(true)} variant="contained">Edit</Button>
        <Button onClick={() => deletePost(post._id)} variant="contained">Delete</Button>
      </Box>
    </Box>
  );
}

export default Post;
