import React, { useState } from "react";
import { Card, CardContent, CardActions, Button, Typography, Box } from '@mui/material';

function Post({ post, editPost, deletePost }) {
  const [editing, setEditing] = useState(false);
  const [title, setTitle] = useState(post.title);
  const [content, setContent] = useState(post.content);
  const [author, setAuthor] = useState(post.author);

  const handleEdit = () => {
    setTitle(post.title);
    setContent(post.content);
    setAuthor(post.author);
    setEditing(true);
  };

  const handleDelete = () => {
    deletePost(post._id);
  };

  const handleSave = () => {
    editPost(post._id, title, content, author);
    setEditing(false);
  };

  if (editing) {
    return (
      <Box sx={{ mb: 2, width: '70%', mx: 'auto' }}>
        <Box sx={{ mb: 1 }}>
          <input
            type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
            placeholder="Author"
            required
          />
        </Box>
        <Box sx={{ mb: 1 }}>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Title"
            required
          />
        </Box>
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="Content"
          required
        />
        <Box sx={{ mt: 1 }}>
          <button onClick={handleSave}>Save</button>
        </Box>
      </Box>
    );
  } else {
    return (
      <Card sx={{ mb: 2, width: '70%', mx: 'auto' }}>
        <CardContent>
          <Typography variant="h5" component="div">{post.title}</Typography>
          <Typography variant="body2" color="text.secondary">{post.content}</Typography>
          <Typography variant="subtitle1" color="text.secondary" align="right">{post.author}</Typography>
        </CardContent>
        <CardActions>
          <Button size="small" onClick={handleEdit}>Edit</Button>
          <Button size="small" onClick={handleDelete}>Delete</Button>
        </CardActions>
      </Card>
    );
  }
}

export default Post;
