import React, { useState } from "react";
import { Card, CardContent, CardActions, Button, Typography, TextField, Box } from '@mui/material';

function Post({ post, editPost, deletePost }) {
  const [editing, setEditing] = useState(false);
  const [title, setTitle] = useState(post.title);
  const [content, setContent] = useState(post.content);
  const [author, setAuthor] = useState(post.author);

  const handleEdit = () => {
    setTitle(post.title);
    setContent(post.content);
    setAuthor(post.author);
    setEditing(true);
  };

  const handleDelete = () => {
    deletePost(post._id);
  };

  const handleSave = () => {
    editPost(post._id, title, content, author);
    setEditing(false);
  };

  if (editing) {
    return (
      <Box component={Card} mb={2}>
        <CardContent>
          <TextField
            label="Author"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
            fullWidth
            multiline
          />
          <TextField
            label="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            fullWidth
            multiline
          />
          <TextField
            label="Content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            fullWidth
            multiline
          />
        </CardContent>
        <CardActions>
          <Button size="small" onClick={handleSave}>Save</Button>
          <Button size="small" onClick={() => setEditing(false)}>Cancel</Button>
        </CardActions>
      </Box>
    );
  } else {
    return (
      <Box component={Card} mb={2}>
        <CardContent>
          <Typography variant="h5" component="div" textAlign="center">{post.title}</Typography>
          <Typography variant="body2" color="text.secondary" textAlign="center">{post.content}</Typography>
          <Typography variant="subtitle1" color="text.secondary" textAlign="right">{post.author}</Typography>
        </CardContent>
        <CardActions>
          <Button size="small" onClick={handleEdit}>Edit</Button>
          <Button size="small" onClick={handleDelete}>Delete</Button>
        </CardActions>
      </Box>
    );
  }
}

export default Post;
